mapper.select:
SELECT

FROM bobogou_basic_qualification a
WHERE id=#{id}

SELECT

FROM bobogou_basic_qualification a

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.create_by = #{currentUser.id}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM bobogou_basic_qualification a

    a.del_flag = #{DEL_FLAG_NORMAL}


            AND a.next_role_id=#{currentUserRoleId}



        OR a.status = "1"
        OR a.status = "2"
        OR a.status = "4"
        OR a.status = "6"




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM bobogou_basic_qualification a

    a.del_flag = #{DEL_FLAG_DELETE}



        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC
