mapper.select:
SELECT

FROM mb_classify a

WHERE a.id=#{id}

SELECT

FROM mb_classify a


    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.name LIKE concat('%',#{name},'%')


        and a.parent_id = #{parentId}


        and a.level = #{level}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM mb_classify a


    a.is_deleted = #{DEL_FLAG_DELETE}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
