mapper.select:
SELECT

FROM mb_goods a
WHERE a.id=#{id}

SELECT

FROM mb_goods a
WHERE a.id=#{id}

SELECT

FROM mb_goods a
WHERE a.is_deleted = 0 AND a.name = #{name}

SELECT

FROM mb_goods a

    a.is_deleted = #{DEL_FLAG_DELETE}


        and a.name = #{name}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM mb_goods a

    a.is_deleted = #{DEL_FLAG_NORMAL}

        AND a.next_role = #{currentUserRole.id}



        AND a.name LIKE concat('%',#{name},'%')


        AND a.factory_id = #{factoryId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM mb_goods a


    a.is_deleted = #{DEL_FLAG_NORMAL}

        AND (a.create_by = #{currentUser.id} OR c.parent_id = #{currentUserRole.id})



        AND a.name LIKE concat('%',#{name},'%')


        AND a.factory_id = #{factoryId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM mb_goods a


    a.is_deleted = #{DEL_FLAG_NORMAL}
    AND a.act_status = 10



        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

mapper.update:
UPDATE mb_goods SET
    used_amount = used_amount + #{usedAmount},
    stock_amount = stock_amount - #{stockAmount}
WHERE id=#{id}

UPDATE mb_goods SET
    used_amount = used_amount - #{usedAmount},
    stock_amount = stock_amount + #{stockAmount}
WHERE id=#{id}

UPDATE mb_goods SET
    act_status = #{actStatus},
    next_role = #{nextRole}
WHERE id=#{id}
