mapper.select:
SELECT

FROM mb_goods a

WHERE a.id=#{id}

SELECT

FROM mb_goods a


    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.name LIKE concat('%',#{name},'%')




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM mb_goods a


    a.is_deleted = #{DEL_FLAG_DELETE}


        and a.name = #{name}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
