mapper.select:
SELECT

FROM sd_order_factory a

WHERE a.id=#{id}

SELECT

FROM sd_order_factory a


    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.tracking_no LIKE concat('%',#{trackingNo},'%')


        AND b.number LIKE concat('%',#{orderNumber},'%')


        AND a.out_status = #{outStatus}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
