SELECT

FROM sd_order_info a



         a.order_id LIKE concat('%',#{orderId},'%')




        and a.goodsId = #{goodsId}


        and a.goodsImage = #{goodsImage}


        and a.goodsName = #{goodsName}


        and a.specification = #{specification}


        and a.amount = #{amount}


        and a.price = #{price}


        and a.totalPrice = #{totalPrice}


        and a.type = #{type}






        Order BY a.create_time DESC

