mapper.select:
SELECT

FROM sd_order a
WHERE a.id=#{id}

SELECT

FROM sd_order a

    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.user_id = #{userId}


        AND a.number LIKE concat('%',#{number},'%')


        AND a.gross_amount = #{grossAmount}


        AND a.integral = #{integral}


        AND a.health_beans = #{healthBeans}


        AND a.address_id = #{addressId}


        AND a.distribution_mode = #{distributionMode}


        AND a.tracking_no LIKE concat('%',#{trackingNo},'%')


        AND a.pay_mode = #{payMode}


        AND a.type = #{type}


        AND a.status = #{status}


        AND a.pay_time = #{payTime}


        AND a.refund_reason LIKE concat('%',#{refundReason},'%')




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM sd_order a

    a.is_deleted = #{DEL_FLAG_DELETE}


        AND a.user_id = #{userId}


        AND a.number LIKE concat('%',#{number},'%')


        AND a.gross_amount = #{grossAmount}


        AND a.integral = #{integral}


        AND a.health_beans = #{healthBeans}


        AND a.address_id = #{addressId}


        AND a.distribution_mode = #{distributionMode}


        AND a.tracking_no LIKE concat('%',#{trackingNo},'%')


        AND a.pay_mode = #{payMode}


        AND a.type = #{type}


        AND a.status = #{status}


        AND a.pay_time = #{payTime}


        AND a.refund_reason LIKE concat('%',#{refundReason},'%')




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
