mapper.select:
SELECT

FROM sd_order a

WHERE a.id=#{id}

SELECT

FROM sd_order a


    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.user_id = #{userId}


        AND a.number LIKE concat('%',#{number},'%')


        AND a.status = #{status}


        AND a.distribution_mode = #{distributionMode}




                AND #{provinceIds} LIKE concat('%',c.province_id,'%')


                AND #{cityIds} LIKE concat('%',c.city_id,'%')


                AND #{areaIds} LIKE concat('%',c.district_id,'%')


                AND #{streetIds} LIKE concat('%',street_id,'%')









        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
