mapper.select:
SELECT

FROM system_province a
WHERE a.id=#{id}

SELECT

FROM system_province a
WHERE a.code=#{code}

SELECT

FROM system_province a

    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.code = #{code}


        and a.name = #{name}


        and a.short_name = #{shortName}


        and a.lng = #{lng}


        and a.lat = #{lat}


        and a.sort = #{sort}


        and a.status = #{status}


        and a.tenant_code = #{tenantCode}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM system_province a

    a.del_flag = #{DEL_FLAG_DELETE}


        and a.code = #{code}


        and a.name = #{name}


        and a.short_name = #{shortName}


        and a.lng = #{lng}


        and a.lat = #{lat}


        and a.sort = #{sort}


        and a.status = #{status}


        and a.tenant_code = #{tenantCode}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC
