mapper.select:
SELECT

FROM md_region_goods a

WHERE a.id=#{id}

SELECT

FROM md_region_goods a


    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND (a.account_id = #{currentUser.id}
        OR i.parent_ids LIKE concat('%',#{currentUserRole.id},'%'))


        AND a.province_id = #{provinceId}


        AND a.city_id = #{cityId}


        AND a.district_id = #{districtId}


        AND a.street_id = #{streetId}


        AND a.goods_id = #{goodsId}


        AND a.account_id = #{accountId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM md_region_goods a


    a.is_deleted = #{DEL_FLAG_DELETE}


        AND a.province_id = #{provinceId}


        AND a.city_id = #{cityId}


        AND a.district_id = #{districtId}


        AND a.street_id = #{streetId}


        AND a.goods_id = #{goodsId}


        AND a.account_id = #{accountId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
