mapper.select:
SELECT

FROM ud_user a
WHERE a.id=#{id}

SELECT

FROM ud_user a
WHERE
a.is_deleted = #{DEL_FLAG_NORMAL}
AND a.phone = #{phone}

SELECT

FROM ud_user a
WHERE
a.is_deleted = #{DEL_FLAG_NORMAL}
AND a.operator = #{operatorId}

SELECT

FROM ud_user a


    a.is_deleted = #{DEL_FLAG_NORMAL}

        AND a.phone LIKE concat('%',#{phone},'%')


        AND a.pssword LIKE concat('%',#{password},'%')


        AND a.nickname LIKE concat('%',#{nickname},'%')


        AND a.sex = #{sex}


        AND a.member = #{member}


        AND a.integral = #{integral}


        AND a.health_beans = #{healthBeans}


        AND a.signInTime = #{signInTime}


        AND a.message_status = #{messageStatus}


        and a.user_agreement = #{userAgreement}


        and a.apply_status = #{applyStatus}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM ud_user a

    a.is_deleted = #{DEL_FLAG_NORMAL}
    AND a.apply_status = '1'

        AND a.next_role = #{currentUserRole.id}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM ud_user a

    a.is_deleted = #{DEL_FLAG_NORMAL}



        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

mapper.update:
UPDATE ud_user SET
    member = #{member},
    apply_status = #{applyStatus},
    vip_expire = #{vipExpire},
    next_role = #{nextRole}
WHERE id=#{id}
