mapper.select:
SELECT

FROM ud_user a
WHERE id=#{id}

SELECT

FROM ud_user a

    a.is_deleted = #{DEL_FLAG_NORMAL}

        a.phone LIKE concat('%',#{phone},'%')


        a.pssword LIKE concat('%',#{password},'%')


        a.nickname LIKE concat('%',#{nickname},'%')


        and a.sex = #{sex}


        and a.member = #{member}


        and a.integral = #{integral}


        and a.health_beans = #{healthBeans}


        and a.collect_number = #{collectNumber}


        and a.signInTime = #{signInTime}



        and a.message_status = #{messageStatus}


        and a.user_agreement = #{userAgreement}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM ud_user a

    a.is_deleted = #{DEL_FLAG_DELETE}


        a.phone LIKE concat('%',#{phone},'%')


        a.pssword LIKE concat('%',#{password},'%')


        a.nickname LIKE concat('%',#{nickname},'%')


        and a.sex = #{sex}


        and a.member = #{member}


        and a.integral = #{integral}


        and a.health_beans = #{healthBeans}


        and a.collect_number = #{collectNumber}


        and a.signInTime = #{signInTime}



        and a.message_status = #{messageStatus}


        and a.user_agreement = #{userAgreement}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
