mapper.select:
SELECT

FROM bd_message a
WHERE id=#{id}

SELECT

FROM bd_message a

    a.is_deleted = #{DEL_FLAG_NORMAL}


        a.title LIKE concat('%',#{title},'%')


        a.outline LIKE concat('%',#{outline},'%')


        a.content LIKE concat('%',#{content},'%')


        and a.type = #{type}


        and a.user_id = #{userId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM bd_message a

    a.is_deleted = #{DEL_FLAG_DELETE}


        a.title LIKE concat('%',#{title},'%')


        a.outline LIKE concat('%',#{outline},'%')


        a.content LIKE concat('%',#{content},'%')


        and a.type = #{type}


        and a.user_id = #{userId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
