mapper.select:
SELECT

FROM ud_user_member a
WHERE id=#{id}

SELECT

FROM ud_user_member a
WHERE a.user_id=#{userId}

SELECT

FROM ud_user_member a

    a.is_deleted = #{DEL_FLAG_NORMAL}


        AND a.user_id = #{userId}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC

SELECT

FROM ud_user_member a

    a.is_deleted = #{DEL_FLAG_DELETE}

        AND and a.sex = #{sex}




        ORDER BY ${orderBy}


        ORDER BY a.create_time DESC
