mapper.select:
SELECT

FROM system_role a
WHERE id=#{id}

SELECT

FROM system_role a


    a.del_flag = #{DEL_FLAG_NORMAL}


        AND a.id != "1"


        and a.name = #{name}


        and a.english_name = #{englishName}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM system_role a

    a.del_flag = #{DEL_FLAG_DELETE}


        and a.name = #{name}


        and a.english_name = #{englishName}




        ORDER BY ${orderBy}


        ORDER BY a.create_date DESC

mapper.update:
UPDATE system_role SET
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}
